package com.ufgov.zc.client.common;import java.util.HashMap;import java.util.Map;import com.ufgov.zc.common.commonbiz.publish.IBaseDataServiceDelegate;import com.ufgov.zc.common.system.RequestMeta;import com.ufgov.zc.common.system.model.LangTrans;public class LangTransMeta {  private static Map langTransMap = new HashMap();  private static Map generatedResIds = new HashMap();     private static IBaseDataServiceDelegate baseDataServiceDelegate = (IBaseDataServiceDelegate) ServiceFactory    .create(IBaseDataServiceDelegate.class, "baseDataServiceDelegate");    static RequestMeta requestMeta = WorkEnv.getInstance().getRequestMeta();  public synchronized static void init(String resId) {    if (generatedResIds.get(resId) == null) {           langTransMap.putAll(baseDataServiceDelegate.getLangTrans(resId, requestMeta));      generatedResIds.put(resId, resId);    }  }  public synchronized static String translate(String resId) {    String temp = resId;    LangTrans langTrans = (LangTrans) langTransMap.get(temp);    if (langTrans != null) {      temp = langTrans.getResNa();    }else{      langTransMap.putAll(baseDataServiceDelegate.getLangTrans(resId, requestMeta));    }        langTrans = (LangTrans) langTransMap.get(temp);    if (langTrans != null) {      temp = langTrans.getResNa();    }        return temp;  }}